#DEFINE BOUNDARY CONDITIONS AT THE HORIZON
function BC_Horizon!(i::Int64,j::Int64,f::Float64,dfdx::Float64,d2fdx2::Float64,dfdy::Float64,d2fdy2::Float64,d2fdxdy::Float64,g::Float64,dgdx::Float64,d2gdx2::Float64,dgdy::Float64,d2gdy2::Float64,d2gdxdy::Float64,h::Float64,dhdx::Float64,d2hdx2::Float64,dhdy::Float64,d2hdy2::Float64,d2hdxdy::Float64,W::Float64,dWdx::Float64,d2Wdx2::Float64,dWdy::Float64,d2Wdy2::Float64,d2Wdxdy::Float64,p::Float64,dpdx::Float64,d2pdx2::Float64,dpdy::Float64,d2pdy2::Float64,d2pdxdy::Float64,R::Matrix{Float64},idx::Int64,WBC::Float64,rh::Float64,A::Float64)
    R[idx+0] = f - 2*dfdx
    R[idx+1] = g + 2*dgdx
    R[idx+2] = dhdx
    R[idx+3] = -WBC + W
    R[idx+4] = dpdx
end

#DEFINE BOUNDARY CONDITIONS AT THE HORIZON - SPIN AS INPUT PARAMETER
function BC_Horizon_Spin!(i::Int64,j::Int64,f::Float64,dfdx::Float64,d2fdx2::Float64,dfdy::Float64,d2fdy2::Float64,d2fdxdy::Float64,g::Float64,dgdx::Float64,d2gdx2::Float64,dgdy::Float64,d2gdy2::Float64,d2gdxdy::Float64,h::Float64,dhdx::Float64,d2hdx2::Float64,dhdy::Float64,d2hdy2::Float64,d2hdxdy::Float64,W::Float64,dWdx::Float64,d2Wdx2::Float64,dWdy::Float64,d2Wdy2::Float64,d2Wdxdy::Float64,p::Float64,dpdx::Float64,d2pdx2::Float64,dpdy::Float64,d2pdy2::Float64,d2pdxdy::Float64,R::Matrix{Float64},idx::Int64,WBC::Float64,rh::Float64,A::Float64)
    R[idx+0] = f - 2*dfdx
    R[idx+1] = g + 2*dgdx
    R[idx+2] = dhdx
    R[idx+3] = W - dWdx
    R[idx+4] = dpdx
end

#DEFINE BOUNDARY CONDITIONS AT THE INFINITY
function BC_Infinity!(i::Int64,j::Int64,f::Float64,dfdx::Float64,d2fdx2::Float64,dfdy::Float64,d2fdy2::Float64,d2fdxdy::Float64,g::Float64,dgdx::Float64,d2gdx2::Float64,dgdy::Float64,d2gdy2::Float64,d2gdxdy::Float64,h::Float64,dhdx::Float64,d2hdx2::Float64,dhdy::Float64,d2hdy2::Float64,d2hdxdy::Float64,W::Float64,dWdx::Float64,d2Wdx2::Float64,dWdy::Float64,d2Wdy2::Float64,d2Wdxdy::Float64,p::Float64,dpdx::Float64,d2pdx2::Float64,dpdy::Float64,d2pdy2::Float64,d2pdxdy::Float64,R::Matrix{Float64},idx::Int64,WBC::Float64,rh::Float64,A::Float64)
    R[idx+0] = -1 + f
    R[idx+1] = -1 + g
    R[idx+2] = -1 + h
    R[idx+3] = W
    R[idx+4] = p
end

#DEFINE BOUNDARY CONDITIONS AT THE INFINITY - SPIN AS INPUT PARAMETER
function BC_Infinity_Spin!(i::Int64,j::Int64,f::Float64,dfdx::Float64,d2fdx2::Float64,dfdy::Float64,d2fdy2::Float64,d2fdxdy::Float64,g::Float64,dgdx::Float64,d2gdx2::Float64,dgdy::Float64,d2gdy2::Float64,d2gdxdy::Float64,h::Float64,dhdx::Float64,d2hdx2::Float64,dhdy::Float64,d2hdy2::Float64,d2hdxdy::Float64,W::Float64,dWdx::Float64,d2Wdx2::Float64,dWdy::Float64,d2Wdy2::Float64,d2Wdxdy::Float64,p::Float64,dpdx::Float64,d2pdx2::Float64,dpdy::Float64,d2pdy2::Float64,d2pdxdy::Float64,R::Matrix{Float64},idx::Int64,WBC::Float64,rh::Float64,A::Float64)
    R[idx+0] = -1 + f
    R[idx+1] = -1 + g
    R[idx+2] = -1 + h
    R[idx+3] = WBC*(1 + dfdx)^2 + dWdx
    R[idx+4] = p
end

