                        #DEFINE BCS AT THE HORIZON
                        if !spin
                            BC_Horizon_Jac!(i,j+1,ff[i,j],dfdx[i,j],d2fdx2[i,j],dfdy[i,j],d2fdy2[i,j],d2fdxdy[i,j],gg[i,j],dgdx[i,j],d2gdx2[i,j],dgdy[i,j],d2gdy2[i,j],d2gdxdy[i,j],hh[i,j],dhdx[i,j],d2hdx2[i,j],dhdy[i,j],d2hdy2[i,j],d2hdxdy[i,j],WW[i,j],dWdx[i,j],d2Wdx2[i,j],dWdy[i,j],d2Wdy2[i,j],d2Wdxdy[i,j],pp[i,j],dpdx[i,j],d2pdx2[i,j],dpdy[i,j],d2pdy2[i,j],d2pdxdy[i,j],J,idx,WBC,rh,A,v,funcidx,type[funcidx],ordx,ordy)
                        else
                            BC_Horizon_Jac_Spin!(i,j+1,ff[i,j],dfdx[i,j],d2fdx2[i,j],dfdy[i,j],d2fdy2[i,j],d2fdxdy[i,j],gg[i,j],dgdx[i,j],d2gdx2[i,j],dgdy[i,j],d2gdy2[i,j],d2gdxdy[i,j],hh[i,j],dhdx[i,j],d2hdx2[i,j],dhdy[i,j],d2hdy2[i,j],d2hdxdy[i,j],WW[i,j],dWdx[i,j],d2Wdx2[i,j],dWdy[i,j],d2Wdy2[i,j],d2Wdxdy[i,j],pp[i,j],dpdx[i,j],d2pdx2[i,j],dpdy[i,j],d2pdy2[i,j],d2pdxdy[i,j],J,idx,WBC,rh,A,v,funcidx,type[funcidx],ordx,ordy)
                        end
                    elseif i==Nx
                        #DEFINE BCS AT INFINITY
                        if !spin
                            BC_Infinity_Jac!(i,j+1,ff[i,j],dfdx[i,j],d2fdx2[i,j],dfdy[i,j],d2fdy2[i,j],d2fdxdy[i,j],gg[i,j],dgdx[i,j],d2gdx2[i,j],dgdy[i,j],d2gdy2[i,j],d2gdxdy[i,j],hh[i,j],dhdx[i,j],d2hdx2[i,j],dhdy[i,j],d2hdy2[i,j],d2hdxdy[i,j],WW[i,j],dWdx[i,j],d2Wdx2[i,j],dWdy[i,j],d2Wdy2[i,j],d2Wdxdy[i,j],pp[i,j],dpdx[i,j],d2pdx2[i,j],dpdy[i,j],d2pdy2[i,j],d2pdxdy[i,j],J,idx,WBC,rh,A,v,funcidx,type[funcidx],ordx,ordy)
                        else
                            BC_Infinity_Jac_Spin!(i,j+1,ff[i,j],dfdx[i,j],d2fdx2[i,j],dfdy[i,j],d2fdy2[i,j],d2fdxdy[i,j],gg[i,j],dgdx[i,j],d2gdx2[i,j],dgdy[i,j],d2gdy2[i,j],d2gdxdy[i,j],hh[i,j],dhdx[i,j],d2hdx2[i,j],dhdy[i,j],d2hdy2[i,j],d2hdxdy[i,j],WW[i,j],dWdx[i,j],d2Wdx2[i,j],dWdy[i,j],d2Wdy2[i,j],d2Wdxdy[i,j],pp[i,j],dpdx[i,j],d2pdx2[i,j],dpdy[i,j],d2pdy2[i,j],d2pdxdy[i,j],J,idx,WBC,rh,A,v,funcidx,type[funcidx],ordx,ordy)
                        end
                    else
                        #DEFINE FIELD EQUATIONS EVERYWHERE ELSE ON THE GRID
                        Field_Eqs_Jac!(i,j+1,ff[i,j],dfdx[i,j],d2fdx2[i,j],dfdy[i,j],d2fdy2[i,j],d2fdxdy[i,j],gg[i,j],dgdx[i,j],d2gdx2[i,j],dgdy[i,j],d2gdy2[i,j],d2gdxdy[i,j],hh[i,j],dhdx[i,j],d2hdx2[i,j],dhdy[i,j],d2hdy2[i,j],d2hdxdy[i,j],WW[i,j],dWdx[i,j],d2Wdx2[i,j],dWdy[i,j],d2Wdy2[i,j],d2Wdxdy[i,j],pp[i,j],dpdx[i,j],d2pdx2[i,j],dpdy[i,j],d2pdy2[i,j],d2pdxdy[i,j],haxis[i],siny[j],cosy[j],d0F[i,j],d1F[i,j],d2F[i,j],d3F[i,j],J,idx,WBC,rh,A,v,funcidx,type[funcidx],ordx,ordy,X[i],Y[j])
                    end
                    idx+=NFields
                end
            end
            ordx+=1
            if mod(v,Nx)==0
                funcidx+=1
                ordx=0
            end
            if mod(v,Nx*NFields)==0
                funcidx=1
                ordx=0
                ordy+=1
            end
        end
        J .= transpose(J)
    end
end

#SOLVE THE NON-LINEAR SYSTEM TO OBTAIN THE SOLUTIONS
function solve_system(f::Field,g::Field,h::Field,W::Field,p::Field,tol::Float64,WBC::Float64,rh::Float64,A::Float64,spin::Bool=false,show_trace::Bool=true,iterations::Int64=30,method = :newton)
    return nlsolve(only_fj!((R,J,a)->residual_jac!(R,J,a,f,g,h,W,p,WBC,rh,A,spin)), vcat(f.a,g.a,h.a,W.a,p.a), ftol=0.0,xtol=tol, show_trace=show_trace, iterations = iterations, method = method)
end
